import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  CurrencyDollarIcon,
  ClipboardDocumentListIcon,
  TableCellsIcon,
  ClockIcon,
  TrendingUpIcon,
  UsersIcon,
} from '@heroicons/react/24/outline';
import { DashboardStats, Order } from '../types';
import LoadingSpinner from '../components/LoadingSpinner';

interface StatCardProps {
  title: string;
  value: string | number;
  icon: React.ComponentType<any>;
  color: string;
  trend?: {
    value: number;
    isPositive: boolean;
  };
}

function StatCard({ title, value, icon: Icon, color, trend }: StatCardProps) {
  return (
    <div className="card">
      <div className="card-body">
        <div className="flex items-center">
          <div className="flex-shrink-0">
            <div className={`p-3 rounded-lg ${color}`}>
              <Icon className="h-6 w-6 text-white" />
            </div>
          </div>
          <div className="ml-4 flex-1">
            <p className="text-sm font-medium text-gray-600">{title}</p>
            <p className="text-2xl font-bold text-gray-900">{value}</p>
            {trend && (
              <div className="flex items-center mt-1">
                <TrendingUpIcon 
                  className={`h-4 w-4 mr-1 ${
                    trend.isPositive ? 'text-green-500' : 'text-red-500 transform rotate-180'
                  }`} 
                />
                <span className={`text-sm ${
                  trend.isPositive ? 'text-green-600' : 'text-red-600'
                }`}>
                  {Math.abs(trend.value)}%
                </span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default function DashboardPage() {
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [recentOrders, setRecentOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading dashboard data
    const loadDashboardData = async () => {
      setLoading(true);

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Mock dashboard stats
      const mockStats: DashboardStats = {
        todayOrders: 45,
        todayRevenue: 2850.50,
        activeOrders: 8,
        availableTables: 12,
        averageOrderTime: 25,
        popularItems: [
          { item: { id: '1', name: 'Margherita Pizza', price: 18.99 } as any, count: 12 },
          { item: { id: '2', name: 'Caesar Salad', price: 14.99 } as any, count: 8 },
          { item: { id: '3', name: 'Grilled Salmon', price: 26.99 } as any, count: 6 },
        ],
      };

      // Load recent orders from localStorage
      const storedOrders = JSON.parse(localStorage.getItem('pos_orders') || '[]');
      const recent = storedOrders
        .sort((a: Order, b: Order) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
        .slice(0, 5);

      setStats(mockStats);
      setRecentOrders(recent);
      setLoading(false);
    };

    loadDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  if (!stats) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Failed to load dashboard data</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Welcome back! Here's what's happening today.</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Today's Revenue"
          value={`$${stats.todayRevenue.toFixed(2)}`}
          icon={CurrencyDollarIcon}
          color="bg-green-500"
          trend={{ value: 12.5, isPositive: true }}
        />
        <StatCard
          title="Today's Orders"
          value={stats.todayOrders}
          icon={ClipboardDocumentListIcon}
          color="bg-blue-500"
          trend={{ value: 8.2, isPositive: true }}
        />
        <StatCard
          title="Active Orders"
          value={stats.activeOrders}
          icon={ClockIcon}
          color="bg-yellow-500"
        />
        <StatCard
          title="Available Tables"
          value={stats.availableTables}
          icon={TableCellsIcon}
          color="bg-purple-500"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Orders */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Recent Orders</h3>
            <Link to="/tables" className="text-sm text-primary-600 hover:text-primary-700">
              View all
            </Link>
          </div>
          <div className="card-body">
            {recentOrders.length === 0 ? (
              <p className="text-gray-500 text-center py-4">No recent orders</p>
            ) : (
              <div className="space-y-3">
                {recentOrders.map((order) => (
                  <div key={order.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div>
                      <p className="font-medium text-gray-900">Table {order.table.number}</p>
                      <p className="text-sm text-gray-500">
                        {order.items.length} items • ${order.total.toFixed(2)}
                      </p>
                    </div>
                    <div className="text-right">
                      <span className={`badge ${
                        order.status === 'completed' ? 'badge-success' :
                        order.status === 'preparing' ? 'badge-warning' :
                        order.status === 'submitted' ? 'badge-info' :
                        'badge-danger'
                      }`}>
                        {order.status}
                      </span>
                      <p className="text-xs text-gray-500 mt-1">
                        {new Date(order.createdAt).toLocaleTimeString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Popular Items */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Popular Items Today</h3>
            <Link to="/menu" className="text-sm text-primary-600 hover:text-primary-700">
              View menu
            </Link>
          </div>
          <div className="card-body">
            <div className="space-y-3">
              {stats.popularItems.map((item, index) => (
                <div key={item.item.id} className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="flex-shrink-0 w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
                      <span className="text-sm font-medium text-primary-600">{index + 1}</span>
                    </div>
                    <div className="ml-3">
                      <p className="font-medium text-gray-900">{item.item.name}</p>
                      <p className="text-sm text-gray-500">${item.item.price}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-medium text-gray-900">{item.count} sold</p>
                    <p className="text-sm text-gray-500">
                      ${(item.count * item.item.price).toFixed(2)}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Quick Actions</h3>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <Link
              to="/tables"
              className="flex flex-col items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <TableCellsIcon className="h-8 w-8 text-primary-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Manage Tables</span>
            </Link>
            <Link
              to="/menu"
              className="flex flex-col items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <ClipboardDocumentListIcon className="h-8 w-8 text-primary-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">View Menu</span>
            </Link>
            <Link
              to="/kitchen"
              className="flex flex-col items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <ClockIcon className="h-8 w-8 text-primary-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Kitchen Orders</span>
            </Link>
            <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg">
              <UsersIcon className="h-8 w-8 text-gray-400 mb-2" />
              <span className="text-sm font-medium text-gray-500">Reports</span>
              <span className="text-xs text-gray-400">Coming Soon</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
