import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Table } from '../types';
import {
  PlusIcon,
  UsersIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
} from '@heroicons/react/24/outline';
import LoadingSpinner from '../components/LoadingSpinner';

interface TableCardProps {
  table: Table;
  onStatusChange: (tableId: string, status: Table['status']) => void;
}

function TableCard({ table, onStatusChange }: TableCardProps) {
  const getStatusColor = (status: Table['status']) => {
    switch (status) {
      case 'available':
        return 'bg-green-100 border-green-200 text-green-800';
      case 'occupied':
        return 'bg-red-100 border-red-200 text-red-800';
      case 'reserved':
        return 'bg-yellow-100 border-yellow-200 text-yellow-800';
      case 'cleaning':
        return 'bg-gray-100 border-gray-200 text-gray-800';
      default:
        return 'bg-gray-100 border-gray-200 text-gray-800';
    }
  };

  const getStatusIcon = (status: Table['status']) => {
    switch (status) {
      case 'available':
        return <CheckCircleIcon className="h-5 w-5" />;
      case 'occupied':
        return <XCircleIcon className="h-5 w-5" />;
      case 'reserved':
        return <ClockIcon className="h-5 w-5" />;
      case 'cleaning':
        return <UsersIcon className="h-5 w-5" />;
      default:
        return <UsersIcon className="h-5 w-5" />;
    }
  };

  const canStartOrder = table.status === 'available' || table.status === 'reserved';

  return (
    <div className="card hover:shadow-md transition-shadow">
      <div className="card-body">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h3 className="text-xl font-bold text-gray-900">Table {table.number}</h3>
            <p className="text-sm text-gray-500">
              <UsersIcon className="h-4 w-4 inline mr-1" />
              Capacity: {table.capacity}
            </p>
            {table.location && (
              <p className="text-sm text-gray-500">{table.location}</p>
            )}
          </div>
          <div className={`px-3 py-1 rounded-full text-sm font-medium flex items-center ${getStatusColor(table.status)}`}>
            {getStatusIcon(table.status)}
            <span className="ml-1 capitalize">{table.status}</span>
          </div>
        </div>

        <div className="space-y-2">
          {canStartOrder && (
            <Link
              to={`/order/${table.id}`}
              className="btn btn-primary w-full"
            >
              <PlusIcon className="h-5 w-5 mr-2" />
              Start Order
            </Link>
          )}

          {table.status === 'occupied' && table.currentOrderId && (
            <Link
              to={`/order/${table.id}`}
              className="btn btn-secondary w-full"
            >
              View Current Order
            </Link>
          )}

          {/* Status change buttons */}
          <div className="flex space-x-2">
            {table.status !== 'available' && (
              <button
                onClick={() => onStatusChange(table.id, 'available')}
                className="flex-1 btn btn-outline text-xs py-2"
              >
                Mark Available
              </button>
            )}
            {table.status !== 'cleaning' && (
              <button
                onClick={() => onStatusChange(table.id, 'cleaning')}
                className="flex-1 btn btn-outline text-xs py-2"
              >
                Mark Cleaning
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default function TablesPage() {
  const [tables, setTables] = useState<Table[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<'all' | Table['status']>('all');

  useEffect(() => {
    // Load tables data
    const loadTables = async () => {
      setLoading(true);

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 500));

      // Mock tables data
      const mockTables: Table[] = [
        { id: '1', number: 1, capacity: 2, status: 'available', location: 'Window side' },
        { id: '2', number: 2, capacity: 4, status: 'occupied', location: 'Center', currentOrderId: 'order-1' },
        { id: '3', number: 3, capacity: 4, status: 'available', location: 'Center' },
        { id: '4', number: 4, capacity: 6, status: 'reserved', location: 'Private area' },
        { id: '5', number: 5, capacity: 2, status: 'cleaning', location: 'Window side' },
        { id: '6', number: 6, capacity: 4, status: 'available', location: 'Center' },
        { id: '7', number: 7, capacity: 8, status: 'available', location: 'Private area' },
        { id: '8', number: 8, capacity: 2, status: 'occupied', location: 'Bar area', currentOrderId: 'order-2' },
        { id: '9', number: 9, capacity: 4, status: 'available', location: 'Center' },
        { id: '10', number: 10, capacity: 6, status: 'available', location: 'Private area' },
        { id: '11', number: 11, capacity: 2, status: 'available', location: 'Window side' },
        { id: '12', number: 12, capacity: 4, status: 'available', location: 'Center' },
      ];

      setTables(mockTables);
      setLoading(false);
    };

    loadTables();
  }, []);

  const handleStatusChange = (tableId: string, status: Table['status']) => {
    setTables(prevTables => 
      prevTables.map(table => 
        table.id === tableId 
          ? { ...table, status, currentOrderId: status === 'available' ? undefined : table.currentOrderId }
          : table
      )
    );
  };

  const filteredTables = filter === 'all' 
    ? tables 
    : tables.filter(table => table.status === filter);

  const getStatusCount = (status: Table['status']) => 
    tables.filter(table => table.status === status).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Tables</h1>
          <p className="text-gray-600">Manage restaurant tables and orders</p>
        </div>
      </div>

      {/* Status Summary */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-green-50 border border-green-200 rounded-lg p-4">
          <div className="flex items-center">
            <CheckCircleIcon className="h-8 w-8 text-green-600" />
            <div className="ml-3">
              <p className="text-sm font-medium text-green-600">Available</p>
              <p className="text-2xl font-bold text-green-900">{getStatusCount('available')}</p>
            </div>
          </div>
        </div>

        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center">
            <XCircleIcon className="h-8 w-8 text-red-600" />
            <div className="ml-3">
              <p className="text-sm font-medium text-red-600">Occupied</p>
              <p className="text-2xl font-bold text-red-900">{getStatusCount('occupied')}</p>
            </div>
          </div>
        </div>

        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <div className="flex items-center">
            <ClockIcon className="h-8 w-8 text-yellow-600" />
            <div className="ml-3">
              <p className="text-sm font-medium text-yellow-600">Reserved</p>
              <p className="text-2xl font-bold text-yellow-900">{getStatusCount('reserved')}</p>
            </div>
          </div>
        </div>

        <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
          <div className="flex items-center">
            <UsersIcon className="h-8 w-8 text-gray-600" />
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-600">Cleaning</p>
              <p className="text-2xl font-bold text-gray-900">{getStatusCount('cleaning')}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Filter Buttons */}
      <div className="flex flex-wrap gap-2">
        <button
          onClick={() => setFilter('all')}
          className={`btn ${filter === 'all' ? 'btn-primary' : 'btn-secondary'}`}
        >
          All Tables ({tables.length})
        </button>
        <button
          onClick={() => setFilter('available')}
          className={`btn ${filter === 'available' ? 'btn-primary' : 'btn-secondary'}`}
        >
          Available ({getStatusCount('available')})
        </button>
        <button
          onClick={() => setFilter('occupied')}
          className={`btn ${filter === 'occupied' ? 'btn-primary' : 'btn-secondary'}`}
        >
          Occupied ({getStatusCount('occupied')})
        </button>
        <button
          onClick={() => setFilter('reserved')}
          className={`btn ${filter === 'reserved' ? 'btn-primary' : 'btn-secondary'}`}
        >
          Reserved ({getStatusCount('reserved')})
        </button>
        <button
          onClick={() => setFilter('cleaning')}
          className={`btn ${filter === 'cleaning' ? 'btn-primary' : 'btn-secondary'}`}
        >
          Cleaning ({getStatusCount('cleaning')})
        </button>
      </div>

      {/* Tables Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {filteredTables.map((table) => (
          <TableCard
            key={table.id}
            table={table}
            onStatusChange={handleStatusChange}
          />
        ))}
      </div>

      {filteredTables.length === 0 && (
        <div className="text-center py-12">
          <UsersIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No tables found</h3>
          <p className="mt-1 text-sm text-gray-500">
            {filter === 'all' ? 'No tables available.' : `No tables with status "${filter}".`}
          </p>
        </div>
      )}
    </div>
  );
}
