import React, { useState, useEffect } from 'react';
import { RepairService, Customer, RepairPart } from '../types';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import LoadingSpinner from '../components/LoadingSpinner';
import {
  PlusIcon,
  MagnifyingGlassIcon,
  EyeIcon,
  PencilIcon,
  CheckIcon,
  ClockIcon,
  WrenchScrewdriverIcon,
  DevicePhoneMobileIcon,
  ComputerDesktopIcon,
  ExclamationTriangleIcon,
  UserIcon,
  CalendarIcon,
  CurrencyRupeeIcon,
} from '@heroicons/react/24/outline';

interface RepairCardProps {
  repair: RepairService;
  onEdit: (repair: RepairService) => void;
  onUpdateStatus: (repairId: string, status: RepairService['status']) => void;
}

function RepairCard({ repair, onEdit, onUpdateStatus }: RepairCardProps) {
  const getStatusColor = (status: RepairService['status']) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'delivered': return 'bg-gray-100 text-gray-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getDeviceIcon = (deviceType: string) => {
    if (deviceType.toLowerCase().includes('phone') || deviceType.toLowerCase().includes('mobile')) {
      return DevicePhoneMobileIcon;
    }
    return ComputerDesktopIcon;
  };

  const DeviceIcon = getDeviceIcon(repair.deviceType);
  const isOverdue = new Date(repair.estimatedCompletion) < new Date() && repair.status !== 'completed' && repair.status !== 'delivered';

  return (
    <div className={`bg-white rounded-lg shadow-md p-4 border-l-4 ${
      isOverdue ? 'border-red-500' : 
      repair.status === 'completed' ? 'border-green-500' :
      repair.status === 'in_progress' ? 'border-blue-500' : 'border-yellow-500'
    }`}>
      <div className="flex justify-between items-start mb-3">
        <div className="flex items-start gap-3">
          <DeviceIcon className="h-6 w-6 text-gray-600 mt-1" />
          <div className="flex-1">
            <h3 className="font-semibold text-gray-900 text-lg">{repair.deviceBrand} {repair.deviceModel}</h3>
            <p className="text-sm text-gray-600 mb-1">{repair.deviceType}</p>
            <p className="text-xs text-gray-500 mb-2">{repair.issueDescription}</p>
            <div className="flex items-center gap-4 text-xs text-gray-500">
              <span className="flex items-center gap-1">
                <UserIcon className="h-3 w-3" />
                {repair.customer.name}
              </span>
              <span className="flex items-center gap-1">
                <CalendarIcon className="h-3 w-3" />
                {new Date(repair.createdAt).toLocaleDateString()}
              </span>
            </div>
          </div>
        </div>
        <div className="text-right">
          <div className="text-xl font-bold text-gray-900">â‚¹{repair.estimatedCost.toLocaleString()}</div>
          <div className="text-sm text-gray-600">Est. Cost</div>
          {isOverdue && (
            <div className="flex items-center text-xs text-red-600 mt-1">
              <ExclamationTriangleIcon className="h-3 w-3 mr-1" />
              Overdue
            </div>
          )}
        </div>
      </div>

      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(repair.status)}`}>
            {repair.status.replace('_', ' ').toUpperCase()}
          </span>
          <span className="text-xs text-gray-500">
            Due: {new Date(repair.estimatedCompletion).toLocaleDateString()}
          </span>
        </div>
        {repair.technician && (
          <span className="text-xs text-gray-600">
            Tech: {repair.technician.name}
          </span>
        )}
      </div>

      {repair.partsUsed && repair.partsUsed.length > 0 && (
        <div className="mb-3">
          <div className="text-xs text-gray-600 mb-1">Parts Used:</div>
          <div className="flex flex-wrap gap-1">
            {repair.partsUsed.map((part, index) => (
              <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 rounded text-xs">
                {part.name} (â‚¹{part.cost})
              </span>
            ))}
          </div>
        </div>
      )}

      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          {repair.status !== 'delivered' && repair.status !== 'cancelled' && (
            <select
              value={repair.status}
              onChange={(e) => onUpdateStatus(repair.id, e.target.value as RepairService['status'])}
              className="text-xs border border-gray-300 rounded px-2 py-1 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="pending">Pending</option>
              <option value="in_progress">In Progress</option>
              <option value="completed">Completed</option>
              <option value="delivered">Delivered</option>
            </select>
          )}
        </div>

        <div className="flex items-center gap-2">
          <button
            onClick={() => onEdit(repair)}
            className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
            title="Edit Repair"
          >
            <PencilIcon className="h-4 w-4" />
          </button>
          <button className="p-2 text-gray-600 hover:bg-gray-50 rounded-lg transition-colors" title="View Details">
            <EyeIcon className="h-4 w-4" />
          </button>
        </div>
      </div>
    </div>
  );
}

interface NewRepairModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (repair: Omit<RepairService, 'id' | 'createdAt' | 'updatedAt'>) => void;
}

function NewRepairModal({ isOpen, onClose, onSubmit }: NewRepairModalProps) {
  const [formData, setFormData] = useState({
    customerId: '',
    deviceType: '',
    deviceBrand: '',
    deviceModel: '',
    issueDescription: '',
    estimatedCost: '',
    estimatedCompletion: '',
  });
  const [customers] = useState<Customer[]>(mockCustomers);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const customer = customers.find(c => c.id === formData.customerId);
    if (!customer) return;

    onSubmit({
      customer,
      deviceType: formData.deviceType,
      deviceBrand: formData.deviceBrand,
      deviceModel: formData.deviceModel,
      issueDescription: formData.issueDescription,
      estimatedCost: parseFloat(formData.estimatedCost),
      estimatedCompletion: new Date(formData.estimatedCompletion),
      status: 'pending',
      technician: null,
      partsUsed: [],
      actualCost: 0,
      notes: '',
    });

    setFormData({
      customerId: '',
      deviceType: '',
      deviceBrand: '',
      deviceModel: '',
      issueDescription: '',
      estimatedCost: '',
      estimatedCompletion: '',
    });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
