import React, { useState, useEffect } from 'react';
import { MenuItem, MenuCategory } from '../types';
import {
  MagnifyingGlassIcon,
  PlusIcon,
  PencilIcon,
  EyeSlashIcon,
  ClockIcon,
} from '@heroicons/react/24/outline';
import LoadingSpinner from '../components/LoadingSpinner';
import { useAuth } from '../contexts/AuthContext';

interface MenuItemCardProps {
  item: MenuItem;
  onEdit?: (item: MenuItem) => void;
  onToggleAvailability?: (itemId: string) => void;
}

function MenuItemCard({ item, onEdit, onToggleAvailability }: MenuItemCardProps) {
  const { user } = useAuth();
  const isAdmin = user?.role === 'admin';

  return (
    <div className={`card hover:shadow-md transition-shadow ${
      !item.isAvailable ? 'opacity-60' : ''
    }`}>
      <div className="card-body">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-2">
              <h3 className="text-lg font-semibold text-gray-900">{item.name}</h3>
              {!item.isAvailable && (
                <span className="badge badge-danger">
                  <EyeSlashIcon className="h-3 w-3 mr-1" />
                  Unavailable
                </span>
              )}
            </div>

            <p className="text-gray-600 text-sm mb-3 line-clamp-2">{item.description}</p>

            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-primary-600">
                ${item.price.toFixed(2)}
              </span>

              <div className="flex items-center text-sm text-gray-500">
                <ClockIcon className="h-4 w-4 mr-1" />
                {item.preparationTime} min
              </div>
            </div>

            {item.allergens && item.allergens.length > 0 && (
              <div className="mt-2">
                <p className="text-xs text-gray-500 mb-1">Allergens:</p>
                <div className="flex flex-wrap gap-1">
                  {item.allergens.map((allergen) => (
                    <span key={allergen} className="px-2 py-1 bg-red-100 text-red-700 text-xs rounded">
                      {allergen}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>

          {item.image && (
            <div className="ml-4 flex-shrink-0">
              <img
                src={item.image}
                alt={item.name}
                className="w-20 h-20 object-cover rounded-lg"
              />
            </div>
          )}
        </div>

        {isAdmin && (
          <div className="mt-4 pt-4 border-t border-gray-200 flex space-x-2">
            <button
              onClick={() => onEdit?.(item)}
              className="btn btn-outline flex-1 text-sm py-2"
            >
              <PencilIcon className="h-4 w-4 mr-1" />
              Edit
            </button>
            <button
              onClick={() => onToggleAvailability?.(item.id)}
              className={`btn flex-1 text-sm py-2 ${
                item.isAvailable ? 'btn-warning' : 'btn-success'
              }`}
            >
              {item.isAvailable ? 'Mark Unavailable' : 'Mark Available'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default function MenuPage() {
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);
  const [categories, setCategories] = useState<MenuCategory[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [showUnavailable, setShowUnavailable] = useState(true);
  const { user } = useAuth();

  const isAdmin = user?.role === 'admin';

  useEffect(() => {
    // Load menu data
    const loadMenuData = async () => {
      setLoading(true);

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 500));

      // Mock categories
      const mockCategories: MenuCategory[] = [
